import React from "react";
import { useFinance } from "../context/FinanceContext";
import { Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  Title,
  Tooltip,
  Legend,
  BarElement,
  CategoryScale,
  LinearScale,
  ArcElement,
} from "chart.js";

ChartJS.register(
  Title,
  Tooltip,
  Legend,
  BarElement,
  CategoryScale,
  LinearScale,
  ArcElement
);

const Dashboard = () => {
  const { transactions } = useFinance();

  // Calculate total income, total expenses, and savings
  const totalIncome = transactions
    .filter((t) => t.type === "Income")
    .reduce((sum, t) => sum + t.amount, 0);
  const totalExpenses = transactions
    .filter((t) => t.type === "Expense")
    .reduce((sum, t) => sum + t.amount, 0);
  const savings = totalIncome - totalExpenses;

  // Categorize expenses for the pie chart
  const categories = transactions.reduce((acc, t) => {
    if (t.type === "Expense") {
      acc[t.category] = (acc[t.category] || 0) + t.amount;
    }
    return acc;
  }, {});

  const categoryData = {
    labels: Object.keys(categories),
    datasets: [
      {
        data: Object.values(categories),
        backgroundColor: [
          "#FF5733",
          "#33FF57",
          "#3357FF",
          "#F9FF33",
          "#FF33F6",
        ],
      },
    ],
  };

  // Prepare data for monthly spending trends bar chart
  const months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];
  const monthlyData = new Array(12).fill(0);
  transactions.forEach((t) => {
    if (t.type === "Expense") {
      const monthIndex = new Date(t.date).getMonth();
      monthlyData[monthIndex] += t.amount;
    }
  });

  const barData = {
    labels: months,
    datasets: [
      {
        label: "Monthly Expenses",
        data: monthlyData,
        backgroundColor: "#FF5733",
      },
    ],
  };

  return (
    <div>
      <h2 className="mb-4">Financial Overview</h2>
      <div className="row">
        <div className="col-md-4">
          <div className="card text-center mb-4">
            <div className="card-body">
              <h5 className="card-title">Total Income</h5>
              <p className="card-text">${totalIncome}</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-center mb-4">
            <div className="card-body">
              <h5 className="card-title">Total Expenses</h5>
              <p className="card-text">${totalExpenses}</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-center mb-4">
            <div className="card-body">
              <h5 className="card-title">Savings</h5>
              <p className="card-text">${savings}</p>
            </div>
          </div>
        </div>
      </div>

      <h3 className="mb-4">Monthly Spending Trends</h3>
      <Bar data={barData} />

      <h3 className="mb-4">Expense Breakdown by Category</h3>
      <Pie data={categoryData} />
    </div>
  );
};

export default Dashboard;
