import React, { useState } from "react";
import { useFinance } from "../context/FinanceContext";
import { Link } from "react-router-dom";

const TransactionHistory = () => {
  const { transactions, deleteTransaction, editTransaction } = useFinance();
  const [filter, setFilter] = useState({
    category: "",
    startDate: "",
    endDate: "",
  });
  const [sortBy, setSortBy] = useState("date");

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilter({
      ...filter,
      [name]: value,
    });
  };

  const filteredTransactions = transactions.filter((t) => {
    const inCategory = filter.category ? t.category === filter.category : true;
    const inDateRange =
      filter.startDate && filter.endDate
        ? new Date(t.date) >= new Date(filter.startDate) &&
          new Date(t.date) <= new Date(filter.endDate)
        : true;
    return inCategory && inDateRange;
  });

  const sortedTransactions = filteredTransactions.sort((a, b) => {
    if (sortBy === "amount") {
      return a.amount - b.amount;
    } else if (sortBy === "date") {
      return new Date(a.date) - new Date(b.date);
    }
    return 0;
  });

  const handleDelete = (id) => {
    deleteTransaction(id);
  };

  return (
    <div>
      <h2>Transaction History</h2>

      <div className="mb-4">
        <h5>Filter Transactions</h5>
        <form className="form-inline">
          <label htmlFor="category" className="mr-2">
            Category:
          </label>
          <input
            type="text"
            id="category"
            name="category"
            className="form-control mr-2"
            placeholder="Category"
            value={filter.category}
            onChange={handleFilterChange}
          />
          <label htmlFor="startDate" className="mr-2">
            Start Date:
          </label>
          <input
            type="date"
            id="startDate"
            name="startDate"
            className="form-control mr-2"
            value={filter.startDate}
            onChange={handleFilterChange}
          />
          <label htmlFor="endDate" className="mr-2">
            End Date:
          </label>
          <input
            type="date"
            id="endDate"
            name="endDate"
            className="form-control mr-2"
            value={filter.endDate}
            onChange={handleFilterChange}
          />
        </form>
      </div>

      <div className="mb-4">
        <h5>Sort By</h5>
        <button
          onClick={() => setSortBy("date")}
          className="btn btn-outline-primary mr-2"
        >
          Date
        </button>
        <button
          onClick={() => setSortBy("amount")}
          className="btn btn-outline-primary"
        >
          Amount
        </button>
      </div>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Date</th>
            <th>Category</th>
            <th>Amount</th>
            <th>Type</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {sortedTransactions.map((transaction) => (
            <tr key={transaction.id}>
              <td>{new Date(transaction.date).toLocaleDateString()}</td>
              <td>{transaction.category}</td>
              <td>{transaction.amount}</td>
              <td>{transaction.type}</td>
              <td>{transaction.description}</td>
              <td>
                <button
                  onClick={() => editTransaction(transaction)}
                  className="btn btn-warning mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(transaction.id)}
                  className="btn btn-danger"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <Link to="/add-transaction" className="btn btn-primary mt-4">
        Add New Transaction
      </Link>
    </div>
  );
};

export default TransactionHistory;
