import React, { useState } from "react";
import { useFinance } from "../context/FinanceContext";

const Budgets = () => {
  const { budgets, setBudgets } = useFinance();
  const [newBudget, setNewBudget] = useState({
    category: "",
    amount: "",
  });

  const [categories, setCategories] = useState([
    "Groceries",
    "Salary",
    "Rent",
    "Utilities",
    "Entertainment",
    "Transportation",
    "Others",
  ]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewBudget({
      ...newBudget,
      [name]: value,
    });
  };

  const handleAddBudget = () => {
    if (newBudget.category && newBudget.amount) {
      setBudgets([...budgets, { ...newBudget, id: new Date().getTime() }]);
      setNewBudget({ category: "", amount: "" });
    }
  };

  const handleDeleteBudget = (id) => {
    setBudgets(budgets.filter((budget) => budget.id !== id));
  };

  const calculateRemainingBudget = (category) => {
    const totalSpent = budgets
      .filter((budget) => budget.category === category)
      .reduce((acc, curr) => acc + parseFloat(curr.amount), 0);
    return totalSpent;
  };

  const calculateProgress = (category) => {
    const budget = budgets.find((budget) => budget.category === category);
    if (budget) {
      const totalSpent = calculateRemainingBudget(category);
      const progress = (totalSpent / parseFloat(budget.amount)) * 100;
      return Math.min(progress, 100);
    }
    return 0;
  };

  return (
    <div>
      <h2>Budgets</h2>

      <div className="mb-4">
        <h5>Add New Budget</h5>
        <div className="form-inline">
          <input
            type="text"
            name="category"
            className="form-control mr-2"
            placeholder="Category"
            value={newBudget.category}
            onChange={handleChange}
          />
          <input
            type="number"
            name="amount"
            className="form-control mr-2"
            placeholder="Amount"
            value={newBudget.amount}
            onChange={handleChange}
          />
          <button onClick={handleAddBudget} className="btn btn-primary">
            Add Budget
          </button>
        </div>
      </div>

      <div>
        <h5>Current Budgets</h5>
        {categories.map((category) => {
          const totalSpent = calculateRemainingBudget(category);
          const progress = calculateProgress(category);
          return (
            <div key={category} className="mb-4">
              <h6>{category}</h6>
              <p>Budget: ${newBudget.amount || 0}</p>
              <p>Spent: ${totalSpent}</p>
              <div className="progress">
                <div
                  className="progress-bar"
                  role="progressbar"
                  style={{ width: `${progress}%` }}
                  aria-valuenow={progress}
                  aria-valuemin="0"
                  aria-valuemax="100"
                >
                  {progress}%
                </div>
              </div>
              <button
                onClick={() => handleDeleteBudget(category)}
                className="btn btn-danger mt-2"
              >
                Delete
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Budgets;
